import React from "react";

// Group logs by date and hour
const groupLogsByDateAndHour = (data) => {
  const grouped = {};
  data.forEach(log => {
    const date = new Date(log._time).toLocaleDateString(); // Get the date
    const hour = new Date(log._time).getHours(); // Get the hour
    const key = `${date} ${hour}`; // Create a key combining date and hour
    grouped[key] = (grouped[key] || 0) + 1; // Increment the count
  });
  return Object.keys(grouped).map(key => {
    const [date, hour] = key.split(" ");
    return {
      date,
      hour,
      count: grouped[key]
    };
  });
};

export const TimelineChartSVG = ({ data }) => {
  const groupedData = groupLogsByDateAndHour(data);
  const maxCount = Math.max(...groupedData.map(d => d.count));

  // Set chart width dynamically based on the number of grouped logs
  const baseWidth = 600; // Base width
  const extraWidthPerBar = 50; // Extra width per bar for better visibility
  const chartWidth = Math.max(baseWidth, groupedData.length * extraWidthPerBar); // Adjust width

  const chartHeight = 200;
  const barWidth = chartWidth / groupedData.length; // Width of each bar
  const yAxisTicks = 5; // Number of ticks on the Y-axis
  const tickSpacing = chartHeight / yAxisTicks; // Spacing between Y-axis ticks

  return (
    <svg width={chartWidth + 50} height={chartHeight + 50}>
      {/* Bars */}
      {groupedData.map((d, i) => {
        const barHeight = (d.count / maxCount) * chartHeight;
        return (
          <rect
            key={i}
            x={i * barWidth + 50} // Adjust x to account for Y-axis
            y={chartHeight - barHeight}
            width={barWidth - 2} // Add spacing between bars
            height={barHeight}
            fill="#4A90E2"
          />
        );
      })}

      {/* X-axis labels */}
      {groupedData.map((d, i) => (
        <text
          key={i}
          x={i * barWidth + barWidth / 2 + 50} // Adjust x to account for Y-axis
          y={chartHeight + 20} // Position of x-axis labels
          textAnchor="middle"
          fontSize="10px"
          fill="#333"
        >
          {`${d.date} ${d.hour}:00`} {/* Display date and hour */}
        </text>
      ))}

      {/* Y-axis ticks and labels */}
      {[...Array(yAxisTicks + 1)].map((_, i) => {
        const value = Math.round((maxCount / yAxisTicks) * i); // Calculate tick value
        return (
          <g key={i}>
            {/* Y-axis ticks */}
            <line
              x1="45"
              y1={chartHeight - i * tickSpacing}
              x2="50"
              y2={chartHeight - i * tickSpacing}
              stroke="black"
            />
            {/* Y-axis labels */}
            <text
              x="40"
              y={chartHeight - i * tickSpacing + 4}
              textAnchor="end"
              fontSize="12px"
            >
              {value}
            </text>
          </g>
        );
      })}

      {/* Y-axis label */}
      <text
        x="20"
        y={chartHeight / 2}
        textAnchor="middle"
        fontSize="12px"
        transform={`rotate(-90, 20, ${chartHeight / 2})`} // Rotate for vertical label
      >
        Logs Count
      </text>
    </svg>
  );
};

export default TimelineChartSVG;
