import React from "react";

// Group logs by hour
const groupLogsByHour = (data) => {
  const grouped = {};
  data.forEach(log => {
    const hour = new Date(log.time).getHours();
    grouped[hour] = (grouped[hour] || 0) + 1;
  });
  return Object.keys(grouped).map(hour => ({
    time: hour,
    count: grouped[hour]
  }));
};

const TimelineChartSVG = ({ data }) => {
  const groupedData = groupLogsByHour(data);
  const maxCount = Math.max(...groupedData.map(d => d.count));

  const chartWidth = 600;
  const chartHeight = 200;
  const barWidth = chartWidth / groupedData.length;

  return (
    <svg width={chartWidth} height={chartHeight}>
      {groupedData.map((d, i) => {
        const barHeight = (d.count / maxCount) * chartHeight;
        return (
          <rect
            key={i}
            x={i * barWidth}
            y={chartHeight - barHeight}
            width={barWidth - 10}
            height={barHeight}
            fill="#4A90E2"
          />
        );
      })}

      {/* X-axis labels */}
      {groupedData.map((d, i) => (
        <text
          key={i}
          x={i * barWidth + barWidth / 2}
          y={chartHeight + 20}
          textAnchor="middle"
          fontSize="12px"
        >
          {`${d.time}:00`}
        </text>
      ))}
    </svg>
  );
};

export default TimelineChartSVG;
